DROP PROCEDURE IF EXISTS `PROC_EC_SYSTEM_SCRIPT_ACCOUNT`;
DELIMITER ;;
CREATE DEFINER=`192.168.6.8`@`%` PROCEDURE `PROC_EC_SYSTEM_SCRIPT_ACCOUNT`(IN `PARA_ACCOUINT` VARCHAR(30),OUT `PARA_RETURN` int)
TOP:BEGIN
-- =============================================
-- AUTHOR:		<AUTHOR,董军伟,NAME>
-- CREATE DATE: <CREATE DATE,20160714,>
-- DESCRIPTION:	<DESCRIPTION,组织启用时需要同步创建该组织所使用到的表,>
-- =============================================

  DECLARE P_TOTAL_INDEX INT ;                     -- 临时表总索引
  DECLARE P_CURRE_INDEX INT ;                     -- 循环到的索引
  DECLARE INDEXID INT ;                     -- 循环到的索引
  DECLARE P_SQL_COL VARCHAR(400);                 -- 主键列标识符
  DECLARE P_SQL_TK  VARCHAR(30);                  -- 动态的表名称
  DECLARE P_SQL_TK1  VARCHAR(30);                  -- 动态的表名称
  DECLARE P_SQL_WA VARCHAR(2000) DEFAULT '' ;     -- 动态脚本函数
  DECLARE PARA_TABLE_KEY VARCHAR(30) DEFAULT '';  -- 动态表脚本键

	DECLARE Done INT DEFAULT 0;
	DECLARE rs CURSOR FOR SELECT TABLE_NAME,TABLE_SCRIPT FROM ERP_SYS_OCODE_SCRIPT;
	DECLARE CONTINUE HANDLER FOR SQLSTATE '02000' SET Done = 1;

  SELECT SYS_TABLE_KEY INTO PARA_TABLE_KEY FROM ERP_EC_OCODE WHERE OCODE=PARA_ACCOUINT LIMIT 1;
  
  IF(IFNULL(PARA_TABLE_KEY,'')!='' AND IFNULL(P_CURRE_INDEX,0)=0)
   THEN
      OPEN rs;  
     REPEAT
					FETCH NEXT FROM rs INTO P_SQL_TK, P_SQL_COL;  
					SET P_SQL_WA:=CONCAT(' DROP TABLE IF EXISTS  ',P_SQL_TK,'_',PARA_TABLE_KEY);
          SET @SQL_TEMP_UYEAR=P_SQL_WA;
				  PREPARE EXEC_SQL_UYEAR FROM @SQL_TEMP_UYEAR;
					EXECUTE EXEC_SQL_UYEAR;
					DEALLOCATE PREPARE EXEC_SQL_UYEAR;

          SET P_SQL_WA=CONCAT('CREATE TABLE ',P_SQL_TK,'_',PARA_TABLE_KEY,' (like ',P_SQL_TK,'_JTLY);');
          
          SET @SQL_TEMP_UYEAR=P_SQL_WA;
				  PREPARE EXEC_SQL_UYEAR FROM @SQL_TEMP_UYEAR;
					EXECUTE EXEC_SQL_UYEAR;
					DEALLOCATE PREPARE EXEC_SQL_UYEAR;
      UNTIL Done END REPEAT;
      /* 关闭游标 */
      CLOSE rs;
      SET PARA_RETURN =1;
   ELSE
     SET PARA_RETURN =0;
END IF;

  IF(PARA_RETURN=1)
   THEN
      UPDATE ERP_EC_OCODE SET ERP_EC_OCODE.STATUS=1 WHERE OCODE=PARA_ACCOUINT ;
   END IF;

	 
END
;;
DELIMITER ;

-- ----------------------------
-- Procedure structure for PROC_EC_SYSTEM_SCRIPT_ACCOUNT_DELETE
-- ----------------------------
DROP PROCEDURE IF EXISTS `PROC_EC_SYSTEM_SCRIPT_ACCOUNT_DELETE`;
DELIMITER ;;
CREATE DEFINER=`root`@`%` PROCEDURE `PROC_EC_SYSTEM_SCRIPT_ACCOUNT_DELETE`(IN PARA_ACCOUINT VARCHAR(30),OUT PARA_RETURN INT)
BEGIN
-- =============================================
-- AUTHOR:		<AUTHOR,董军伟,NAME>
-- CREATE DATE: <CREATE DATE,20160714,>
-- DESCRIPTION:	<DESCRIPTION,组织启用时需要同步创建该组织所使用到的表,>
-- =============================================
  DECLARE P_TOTAL_INDEX INT ;                     -- 临时表总索引
  DECLARE P_CURRE_INDEX INT ;                     -- 循环到的索引
  DECLARE P_SQL_COL VARCHAR(400);                 -- 主键列标识符
  DECLARE P_SQL_TK  VARCHAR(30);                  -- 动态的表名称
  DECLARE P_SQL_WA VARCHAR(2000) DEFAULT '' ;     -- 动态脚本函数
  DECLARE PARA_TABLE_KEY VARCHAR(30) DEFAULT '';  -- 动态表脚本键
  DECLARE P_STATUS  INT;
  SELECT SYS_TABLE_KEY,IFNULL(STATUS,0) INTO PARA_TABLE_KEY,P_STATUS FROM ERP_EC_OCODE WHERE OCODE=PARA_ACCOUINT LIMIT 1;
  IF(IFNULL(PARA_TABLE_KEY,'')!='' AND IFNULL(P_STATUS,0)=0)
   THEN
      DROP TABLE IF EXISTS TABLE_SCRIPT;
      CREATE TEMPORARY TABLE IF NOT EXISTS TABLE_SCRIPT(ID INT NOT NULL PRIMARY KEY  AUTO_INCREMENT,TABLE_NAME VARCHAR(36),TABLE_SCRIPT VARCHAR(8000));
      
      SET P_CURRE_INDEX:=1;
      SELECT COUNT(*) INTO P_TOTAL_INDEX FROM TABLE_SCRIPT;
       WHILE(IFNULL(P_TOTAL_INDEX,0)>=P_CURRE_INDEX)
        DO
          SELECT TABLE_NAME,TABLE_SCRIPT INTO P_SQL_TK,P_SQL_COL FROM TABLE_SCRIPT WHERE ID=P_CURRE_INDEX;
          SET P_SQL_WA=CONCAT('DROP TABLE IF EXISTS ',P_SQL_TK,'_',PARA_TABLE_KEY);
          SET @SQL_TEMP_UYEAR=P_SQL_WA;
				  PREPARE EXEC_SQL_UYEAR FROM @SQL_TEMP_UYEAR;
					EXECUTE EXEC_SQL_UYEAR;
					DEALLOCATE PREPARE EXEC_SQL_UYEAR;
          SET P_CURRE_INDEX:=P_CURRE_INDEX+1;
       END WHILE;
       DROP TABLE IF EXISTS TABLE_SCRIPT;
       UPDATE ERP_EC_OCODE SET DEL_FLAG=1 WHERE OCODE=PARA_ACCOUINT;
       SET PARA_RETURN =1;
    ELSE
      SET PARA_RETURN =0;
  END IF;
END
;;
DELIMITER ;

