			DO
				-- 获取数据
				SELECT ITEMNO,WAREHOUSE,IFNULL(RELLINEID,1)   INTO P_FTYPE,P_FORMATSTR,P_STEPVALUE   FROM SYS_COMMON_TABLE WHERE ID=GUID_TEMP AND LINEID=P_INDEX ;
				 IF P_FTYPE=0 -- 自定义
				 THEN
					 SET   P_CODESTR=CONCAT(P_CODESTR,P_FORMATSTR);

				 ELSEIF P_FTYPE=1 -- 日期
				 THEN
					 SET  P_CODESTR=CONCAT(P_CODESTR,DATE_FORMAT(NOW(), '%y%m%d'));
				 ELSEIF P_FTYPE=2 -- 流水号
				 THEN
					 SET P_LSCOUNT=P_FORMATSTR;
					 SET  P_CODESTR=CONCAT(P_CODESTR,LPAD('_',P_FORMATSTR, '_'));
				 ELSEIF P_FTYPE=3 -- 组织
				 THEN
					-- SET P_LSCOUNT=P_FORMATSTR;
					 SET  P_CODESTR=CONCAT(P_CODESTR,P_ocode );
				 ELSEIF P_FTYPE=4 -- 用户
				 THEN
					-- SET P_LSCOUNT=P_FORMATSTR;
					 SET  P_CODESTR=CONCAT(P_CODESTR,P_userId);
         ELSEIF P_FTYPE=5 -- 动态单据号
				 THEN
					 SET  P_CODESTR=CONCAT(P_CODESTR,P_GUDING);
				 ELSEIF P_FTYPE=6 -- 职能机构
				 THEN
           select SUBSTRING_INDEX(DEPTNO,'.',-1) INTO P_DEPTSTR  from sys_user where USERID=P_userId;
					 SET  P_CODESTR=CONCAT(P_CODESTR,P_DEPTSTR);
					ELSEIF P_FTYPE=7 -- 动态参数
				 THEN
				 IF IFNULL(P_DSTR,'')<>''
				 THEN
						 SET   P_CODESTR=CONCAT(P_CODESTR,P_DSTR);
				 END IF;		
				 END IF;
         
					SET P_INDEX=P_INDEX+1;

			 END WHILE;

			 


			 SELECT COUNT(1) INTO @COUNT FROM ERP_SYS_OCODE_SCRIPT WHERE TABLE_NAME=P_TALBENAME;
			 IF IFNULL(@COUNT,0)>0
			 THEN
					SELECT SYS_TABLE_KEY INTO P_TABLE_KEY   FROM EC_OCODE WHERE OCODE=P_ocode   LIMIT 1; 
					IF IFNULL(P_TABLE_KEY,'')!=''
					THEN
							SET 	P_TABLE_KEY= CONCAT('_',P_TABLE_KEY);
					END IF;
			 END IF;


			 SET @BILLNO='';
			 SET P_SQLSTRING=CONCAT('SELECT ',P_FIELDNAME  ,' INTO @BILLNO FROM ',P_TALBENAME,IFNULL(P_TABLE_KEY,''),' WHERE TRIM(',P_FIELDNAME,') LIKE ''',P_CODESTR,''' order by  ABS(',P_FIELDNAME,') desc,',P_FIELDNAME,' desc limit 1');
			 SET @SQL_TEMP_WA=P_SQLSTRING;
			 PREPARE EXEC_SQL FROM @SQL_TEMP_WA;
			 EXECUTE EXEC_SQL;
			 DEALLOCATE PREPARE EXEC_SQL;


			 SELECT INSTR(P_CODESTR,'_') INTO P_LSINDEX;
			 SELECT  SUBSTRING(@BILLNO,P_LSINDEX,P_LSCOUNT) INTO P_LSNUM;
		 
			 SELECT  LPAD(IFNULL(P_LSNUM,0)+P_STEPVALUE,P_LSCOUNT,'0') INTO P_LSNUM;
			 SET IDNUM=CONCAT(SUBSTRING(P_CODESTR,1,P_LSINDEX-1),P_LSNUM,SUBSTRING(P_CODESTR,P_LSINDEX+P_LSCOUNT));
		    DELETE FROM SYS_COMMON_TABLE WHERE ID=GUID_TEMP;
			-- SELECT IDNUM;
    ELSE
      set IDNUM='';
     
     
    END IF;
			/*DROP TABLE TEMP_CODERULEDETAIL;*/
 
 
END
;;
DELIMITER ;

