DROP PROCEDURE IF EXISTS `导出数据表结构`;
DELIMITER ;;
CREATE DEFINER=`192.168.6.8`@`%` PROCEDURE `导出数据表结构`()
BEGIN

DROP TABLE if EXISTS ljsdaochubiao;
SET @INDEXED:=0;
CREATE TABLE ljsdaochubiao
SELECT  (@INDEXED:=@INDEXED+1) id,c.TABLE_NAME,CONCAT(c.TABLE_NAME,'(',c.TABLE_COMMENT,')') biaomingcheng from information_schema.`TABLES`  c
where TABLE_SCHEMA='9d-erp' and TABLE_NAME LIKE 'erp_%' 
ORDER BY c.TABLE_NAME DESC;

SET @account:=0;
SET @INDEXED:=1;
SELECT COUNT(*) INTO @account from ljsdaochubiao;

DROP TABLE IF EXISTS `ljslishicunchubiao`;
CREATE TABLE ljslishicunchubiao (
  `lineid` int(11) NOT NULL AUTO_INCREMENT,
	biao VARCHAR(50),
   biaoming VARCHAR(50),
  `lie` varchar(100) DEFAULT NULL,
  `leixing` varchar(50) DEFAULT NULL,
  `mingcheng` varchar(200) DEFAULT NULL,
  PRIMARY KEY (`lineid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


WHILE(IFNULL(@INDEXED,0)<=IFNULL(@account,0))
DO
	SET @biao:='';
	SET @biaomingcheng:='';
	SELECT TABLE_NAME INTO @biao from ljsdaochubiao where id=@INDEXED;
	SELECT biaomingcheng INTO @biaomingcheng from ljsdaochubiao where id=@INDEXED;


SET @sqls='';
SET @sqls:=CONCAT('
					SELECT 
						t.TABLE_NAME,
						t.TABLE_COMMENT,
						c.COLUMN_NAME,
						c.COLUMN_TYPE,
						c.COLUMN_COMMENT
					FROM
						information_schema.`COLUMNS` c
					INNER JOIN information_schema.`TABLES` t ON c.TABLE_NAME = t.TABLE_NAME
					AND c.TABLE_SCHEMA = t.TABLE_SCHEMA
					WHERE
						c.TABLE_SCHEMA = ''9d-erp''
					AND c.TABLE_NAME =''',@biao,'''');

			SET @SQL_TEMP_CENTER:=@sqls;
			PREPARE EXEC_SQL FROM @SQL_TEMP_CENTER;
			EXECUTE EXEC_SQL;

      SET @INDEXED:=@INDEXED+1;
	END WHILE;


SELECT (@ROWS :=@ROWS + 1) ROWNUM,lie,leixing,mingcheng from ljslishicunchubiao;

END
;;
DELIMITER ;
